import React, { useEffect, useState } from "react";
import { SafeAreaView, View, TextInput, Text } from "react-native";
import { styles } from "../styles/styles";
import { FlatList } from "react-native";
import DrawingItem from "../components/DrawingItem";

type Drawing = {
  id: string;
  name: string;
  image: string;
};

function DocumentationScreen() {
  const [data, setData] = useState<Drawing[]>([]);
  const [term, setTerm] = useState("");

  useEffect(() => {
    fetch("http://127.0.0.1:5000/drawing", { method: "GET" })
      .then((response) => response.json())
      .then((data) => setData(data.drawings))
      .catch((error) => console.error(error));
    console.log(data);
  }, []);

  const dataa = [
    [1, "tube"],
    [2, "tube2"],
    [3, "tube3"],
  ];

  return (
    <SafeAreaView style={styles.app}>
      <TextInput
        style={styles.search}
        onChangeText={setTerm}
        value={term}
        placeholder="Search..."
      />
      <FlatList
        style={{ display: "flex", flex: 1 /*backgroundColor: "gray"*/ }}
        data={
          term === ""
            ? data
            : data.filter((i) => i[1].includes(term.toLowerCase()))
        }
        keyExtractor={(_, index) => index.toString()}
        renderItem={({ item }) => (
          <DrawingItem
            item={{
              id: item[0],
              name: item[1],
              image: item[2],
            }}
          />
        )}
      />
    </SafeAreaView>
  );
}

export default DocumentationScreen;
