import React, { useState } from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import CameraScreen from "./src/screens/CameraScreen";
import SettingsScreen from "./src/screens/SettingsScreen";
import { Ionicons } from "@expo/vector-icons";
import DocumentationScreen from "./src/screens/DocumentaionScreen";
import "./i18n.ts"; // ts => import './i18n.ts'
import { useTranslation } from "react-i18next";

export default function App() {
  const Tab = createBottomTabNavigator();

  const {
    t,
    i18n: { changeLanguage, language },
  } = useTranslation();
  const [currentLanguage, setCurrentLanguage] = useState(language);

  const handleChangeLanguage = () => {
    const newLanguage = currentLanguage === "en" ? "sk" : "en";
    setCurrentLanguage(newLanguage);
    changeLanguage(newLanguage);
  };

  return (
    <NavigationContainer>
      <Tab.Navigator
        initialRouteName="LandingScreen"
        screenOptions={({ route }) => ({
          tabBarIcon: ({ focused, color, size }) => {
            let iconName;

            if (route.name === "LandingScreen") {
              iconName = focused ? "home" : "home-outline";
            } else if (route.name === "SettingsScreen") {
              iconName = focused ? "settings" : "settings-outline";
            } else if (route.name === "CameraScreen") {
              iconName = focused ? "camera" : "camera-outline";
            } else if (route.name === "DocumentationScreen") {
              iconName = focused ? "document-text" : "document-text-outline";
            }
            // You can return any component that you like here!
            return <Ionicons name={iconName} size={size} color={color} />;
          },
          tabBarActiveTintColor: "blue",
          tabBarInactiveTintColor: "gray",
        })}
      >
        <Tab.Screen
          name="DocumentationScreen"
          component={DocumentationScreen}
          options={{
            title: "Documentation",
            // headerTintColor: "white",
            // headerStyle: { backgroundColor: "cyan" },
          }}
        />
        <Tab.Screen
          name="SettingsScreen"
          component={SettingsScreen}
          options={{ title: "Settings" }}
        />

        {/* <Tab.Screen
          name="LandingScreen"
          component={LandingScreen}
          options={{ title: "Home" }}
        /> */}
        <Tab.Screen
          name="CameraScreen"
          component={CameraScreen}
          options={{ title: "Camera" }}
        />
      </Tab.Navigator>
    </NavigationContainer>
  );
}
